
public class Chapter1Exercise {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
//Display the area of a circle radius 5.5
//Area of a circle = radius * radius * pi (3.14159)
		double radius = 5.5;
		double pi = 3.14159;
		
		System.out.println("The area of the circle " + " is " + radius * radius * pi);
		
//Display the perimeter of a circle	
//Perimeter of a circle = 2 * radius * pi
		
		System.out.println("The perimeter of the circle " + " is " + 2 * radius * pi );
		
	}

}
